import requests
import  json
from datetime import datetime
from org.apache.nifi.processor.io import OutputStreamCallback
flowfile = session.get() 

times="start time: "+datetime.now().strftime("%Y-%m-%d %H:%M:%S")+" "


if (flowfile != None):
	db_schema=flowfile.getAttribute('schema')
	column_name=flowfile.getAttribute('column_name')[1:-1]
	column_type=flowfile.getAttribute('column_type')[1:-1]
	column_id=flowfile.getAttribute('column_id')[1:-1]
	column_name= list(column_name.split(",")) 
   	column_id= list(column_id.split(",")) 
   	column_type= list(column_type.split(",")) 


	post_headers={
    		'Content-Type': 'application/json;charset=UTF-8',
    		'Accept': 'application/json;charset=UTF-8'
	}

	leng=int(len(column_name))

	bulk_json=[]

	for colNum in range(0,leng):
		json_to_Atlas={
    			"typeName":"DbColumn",
    			"attributes": {
    				"name" :column_name[colNum][1:-1].encode('utf-8'),
    				"type" : column_type[colNum][1:-1].encode('utf-8'),
    				"position" :column_id[colNum].encode('utf-8') ,
    				"qualifiedName":(column_name[colNum][1:-1]+"_"+db_schema).encode('utf-8')
				}
  			}

		bulk_json.append(json_to_Atlas)


	bulk_json={"entities":bulk_json}
	bulk_json=json.dumps(bulk_json,ensure_ascii=False)
	r=requests.post("http://admin:admin@hdpsecured.amdocs.com:21000/api/atlas/v2/entity/bulk",bulk_json,headers=post_headers)


	times+="endtime: "+datetime.now().strftime("%Y-%m-%d %H:%M:%S")+" "+str(r.text)+" "+str(r.status_code)
	class PyOutputStreamCallback(OutputStreamCallback):
 		def __init__(self):
	       		pass
  		def process(self, outputStream):
   			outputStream.write(bytearray(times.encode('utf-8')))
	flowfile = session.write(flowfile, PyOutputStreamCallback())


	if( '20' in str(r.status_code)):
		session.transfer(flowfile, REL_SUCCESS)
	else:
		session.transfer(flowfile, REL_FAILURE)

