import requests
import  json
import time
from datetime import datetime
from org.apache.nifi.processor.io import OutputStreamCallback
#here is the command to take the flowfile from the queue
flowfile = session.get() 


#sometimes the script cant take the flowfile so im doing this if 
if (flowfile != None):
    #getting the flowfile attributes - its coming with square-brackets so i'm getting rid of them with [1:-1]  they are all strings
    URL = "http://admin:admin@hdpsecured.amdocs.com:21000"
    data_source=flowfile.getAttribute('data_source')
    column_name=flowfile.getAttribute('column_name')
    column_type=flowfile.getAttribute('column_type')
    file_path=flowfile.getAttribute('file_path')
    #to be able to work with each atrribute in the column lists i'm casting those strings to lists
    column_name= json.loads(column_name)
    column_type= json.loads(column_type)
    file_type=(file_path.split(".",1)[1]).upper()


    table_name=flowfile.getAttribute('table_name')
    hdfs_path=flowfile.getAttribute('hdfs_path')
    cols=[]
    #objects declaration to future parts of the script
    guids={}
    guid_list=[]
    bulk_json=[]

    #the headers for the post command - it is mandatory
    post_headers = {
        'Content-Type': 'application/json;charset=UTF-8',
        'Accept': 'application/json;charset=UTF-8'
    }

    leng = len(column_name)

    # running on every column and creating the proper json to the atlas insertion
    try:
        for colNum in range(0,len(column_name)):
            json_to_Atlas = {
                "typeName": "DbColumnType",
                "attributes": {
                    "name": column_name[colNum],
                    "type": column_type[colNum],
                    "position": colNum+1,
                    "qualifiedName": column_name[colNum] + "_" + table_name + "_" + data_source
                }
            }
            bulk_json.append(json_to_Atlas)


        bulk_json = {"entities": bulk_json}
        bulk_json = json.dumps(bulk_json, ensure_ascii=False)

        # this is the post request
        r = requests.post(URL+"/api/atlas/v2/entity/bulk", bulk_json,
                          headers=post_headers)
        if r.status_code / 100 != 2:
            json_text = bulk_json
            raise Exception("Atlas returned code " + r.status_code + " " + r.text)

        response_text = r.text

        # i'm taking the guids and assigning them as attribute to the flow file to relate the rdbms table to those columns
        response_text_json = json.loads(response_text)
        guid_list = []
        for guid in response_text_json["guidAssignments"].values():
            guid_list.append(guid)

        # creating the hdfs_path entity in atlas
        hdfs_to_atlas = {
            "entity": {
                "typeName": "hdfs_path",
                "attributes": {
                    "name": table_name + "_" + data_source,
                    "path": hdfs_path,
                    "qualifiedName": data_source+"_"+file_path+"_"+table_name
                }
            }
        }
        hdfs_to_atlas = json.dumps(hdfs_to_atlas, ensure_ascii=False)
        r = requests.post(URL+"/api/atlas/v2/entity", hdfs_to_atlas,
                          headers=post_headers)
        if r.status_code / 100 != 2:
            json_text = hdfs_to_atlas
            raise Exception("Atlas returned code " + r.status_code + " " + r.text)
        # taking the hdfs path guid for the rdbms table
        hdfs_guid = str(r.text)

        # clearing the unneeded content
        guid_loc = hdfs_guid.find("guidAssignments") + 17
        hdfs_guid = hdfs_guid[guid_loc:-1]
        hdfs_guid = list(json.loads(hdfs_guid, encoding='utf-8').values())


        # the rdbms json
        rdbms_to_atlas = {
            "entity": {
                "typeName": "RDBMS_Table",
                "attributes": {
                    "name": table_name,
                    "data_source_type": "FILE",
                    "last_update": int(time.time() * 1000),
                    "qualifiedName": table_name+"_"+data_source,
                    "data_source": {
                        "file_descriptor": {
                            "file_type":file_type ,
                            "source_name": data_source
                        },
                        "source_type": "FILE"
                    },
                    "hdfs_location": {
                        "typeName": "hdfs_path",
                        "guid": hdfs_guid[0]
                    }
                }
            }
        }

        # it is commented because  the columns handler is not working right now

        for guid in range(0, len(guid_list)):
            location = {"typeName": "DbColumnType", "guid": str(guid_list[guid])}
            cols.append(location)
        rdbms_to_atlas["entity"]["attributes"]["columns"] = cols

        rdbms_to_atlas = json.dumps(rdbms_to_atlas, ensure_ascii=False)
        r = requests.post(URL+"/api/atlas/v2/entity", rdbms_to_atlas,
                          headers=post_headers)
        if r.status_code / 100 != 2:
            json_text = rdbms_to_atlas
            raise Exception("Atlas returned code " + r.status_code + " " + r.text)

        class PyOutputStreamCallback(OutputStreamCallback):
            def __init__(self):
                    pass
            def process(self, outputStream):                
                outputStream.write("Atlas RDBMS Table created - "+data_source+ "." + table_name+" at "+datetime.now().strftime("%Y-%m-%d %H:%M:%S"))

        flowfile = session.write(flowfile, PyOutputStreamCallback())    

        session.transfer(flowfile, REL_SUCCESS)
    except Exception as ERROR:
        class PyOutputStreamCallback(OutputStreamCallback):
            def __init__(self):
                    pass
            def process(self, outputStream):                
                outputStream.write("there has been error at: "+datetime.now().strftime("%Y-%m-%d %H:%M:%S")+"message- "+str(ERROR))
        flowfile = session.write(flowfile, PyOutputStreamCallback())
    	session.transfer(flowfile, REL_FAILURE)
