import org.apache.commons.io.IOUtils
import java.nio.charset.*
import java.text.SimpleDateFormat

import org.apache.poi.ss.usermodel.Cell
import org.apache.poi.ss.usermodel.Row
import org.apache.poi.ss.usermodel.Sheet
import org.apache.poi.ss.usermodel.Workbook
import org.apache.poi.ss.usermodel.WorkbookFactory
import java.util.regex.Pattern

def flowFile = session.get()
if(!flowFile) return

def date = new Date()

flowFile = session.write(flowFile, {inputStream, outputStream ->
    try {
	Workbook wb = WorkbookFactory.create(inputStream,);
	Sheet mySheet = wb.getSheetAt(0);
	def record = ''

	Pattern numbers = Pattern.compile("(^[0-9]*\$)|(^[0-9]*[.][0-9]*\$)")

	Iterator<Row> rowIter = mySheet.rowIterator();
	def rowNum = 0
		while (rowIter.hasNext()) {
			rowNum++
	        	Row nextRow = rowIter.next();
	            	Iterator<Cell> cellIterator = nextRow.cellIterator();

	        	while (cellIterator.hasNext()) {
	                	Cell cell = cellIterator.next()

	                	if ((cell.getCellType().name().equals("NUMERIC"))&& (numbers.matcher(cell.toString()).matches())){
                			double dblNum=(double)cell.getNumericCellValue()
                			int intNum=(int)cell.getNumericCellValue()
                			if (dblNum-intNum==0.0) {
                    				record = record + (intNum.toString()) + ',';
                			}
                			else {
                    				record = record + (cell.toString()) + ',';
                			}
           			}
				else {
					try {
                				String temp=new SimpleDateFormat("dd/MM/yyyy HH:mm:ss").format(cell.getDateCellValue())
                				record = record + temp + ','
           				 }
           			 	catch (e){
                				record = record + (cell.toString()) + ','
           			 	}
				}
	            	}	            

	            	if(rowNum > 0){
	            	// insert tstamp, row num, drop last comma and add end line.  
			// Note: tstamp + row num are composite key
	            	record = record[0..-2] + '\n'
		            outputStream.write(record.getBytes(StandardCharsets.UTF_8))
	            }
	            record = ''
	        }

    }
    catch(e) {
    	log.error("Error during processing of spreadsheet name = xx, sheet = xx", e)
    	//session.transfer(inputStream, REL_FAILURE)
    }
} as StreamCallback)

def filename = flowFile.getAttribute('filename').split('\\.')[0] + '_' + new SimpleDateFormat("YYYYMMdd-HHmmss").format(date)+'.csv'
flowFile = session.putAttribute(flowFile, 'filename', filename) 

session.transfer(flowFile, REL_SUCCESS)

import org.apache.commons.io.IOUtils
import java.nio.charset.StandardCharsets
import com.jcraft.jsch.Channel
import com.jcraft.jsch.ChannelSftp
import com.jcraft.jsch.JSch
import com.jcraft.jsch.Session
import java.util.regex.Pattern
import java.util.regex.Matcher



try{
    flowFile=session.get()
    if(flowFile){

	SFTPHOST =flowFile.getAttribute('data_source')
	SFTPPORT = 22
	SFTPUSER = flowFile.getAttribute('sftp_user')
	SFTPPASS = flowFile.getAttribute('sftp_pass')
	SFTPDIR=flowFile.getAttribute('file_path')
        regex= flowFile.getAttribute('file_name')

	Session sftpSession = null;
	Channel channel = null;
	ChannelSftp channelSftp = null;

	JSch jsch = new JSch();
	sftpSession = jsch.getSession(SFTPUSER, SFTPHOST, SFTPPORT);
	sftpSession.setPassword(SFTPPASS);
	Properties config = new Properties();
	config.put("StrictHostKeyChecking", "no");
	sftpSession.setConfig(config);
	sftpSession.connect();
	channel = sftpSession.openChannel("sftp");
	channel.connect();
	channelSftp = (ChannelSftp) channel;

	channelSftp.cd(SFTPDIR)

	Pattern pattern = Pattern.compile(regex)

	Vector<ChannelSftp.LsEntry> filenames= channelSftp.ls(SFTPDIR);

	attributes=['table_name':' ' , 'file_name':' ' , 'filename' :' ' , 'raw_zone':' ' , 'landing_path':' ' , 'processed_path': ' ' , 'hdfs_path':' ' , 'file_path': ' ']

	flowFiles = [] as List<FlowFile>

	file_name="test123"

	hdfs_path= flowFile.getAttribute('hdfs_path')
	hdfs_path=hdfs_path.substring(0,hdfs_path.indexOf(regex))

 	landing_path=flowFile.getAttribute('landing_path')
	landing_path=landing_path.substring(0,landing_path.indexOf(regex))

	processed_path=flowFile.getAttribute('processed_path')
	processed_path=processed_path.substring(0,processed_path.lastIndexOf("/"))

        raw_zone= flowFile.getAttribute('raw_zone')
	raw_zone=raw_zone.substring(0,raw_zone.indexOf(regex))

        for(ChannelSftp.LsEntry entry: filenames){
	   Matcher m = pattern.matcher(entry.getFilename())
    	   if (m.matches()){

                newFlowFile= session.create(flowFile)

		file_name=entry.getFilename()
		attributes["file_name"]=file_name
		attributes["filename"]=file_name
		attributes["file_path"]=SFTPDIR+"/"+file_name


		table_name=file_name.substring(0,file_name.indexOf('.'))
		attributes["table_name"]=table_name

		attributes["landing_path"]=landing_path+""+table_name+"/"+file_name
		attributes["hdfs_path"]=hdfs_path+""+table_name+"/"+file_name
		attributes["raw_zone"]=raw_zone+""+table_name+"/"+file_name
		attributes["processed_path"]=processed_path+"/"+file_name


		newFlowFile=session.putAllAttributes(newFlowFile,attributes)


		flowFiles << newFlowFile
            }

	}
            session.remove(flowFile)
            session.transfer(flowFiles, REL_SUCCESS)
    }
    else{
	return
    }
}
catch(Exception e){
    session.remove(flowFiles)
    flowFile= session.write(flowFile, {outputStream -> outputStream.write(e.toString().getBytes(StandardCharsets.UTF_8)) } as OutputStreamCallback)
    session.transfer(flowFile, REL_FAILURE)
}

