        hive_db = flowfile.getAttribute('data_source').replace('.','_')
	hive_db=hive_db.replace('.','_')

    URL = "https://illinhdpmdev02.corp.amdocs.com:21443"

    avro_schema = flowfile.getAttribute('avro.schema')
    avro_schema = json.loads(avro_schema)

    table_name = flowfile.getAttribute('table_name')

    text=""
    cols = []
    guid_list = []
    bulk_json = []

    post_headers = {
        'Content-Type': 'application/json;charset=UTF-8',
        'Accept': 'application/json;charset=UTF-8'
    }


    try:
        for column in avro_schema["fields"]:
            columns_json = {
                "typeName": "hive_column",
		"createTime": int(time.time() * 1000),
		"modifiedTime": int(time.time() * 1000),
                "attributes": {
                    "name": column["name"],
		    "owner":"nifi",
                    "type": json.dumps(column["type"]),
                    "position": (avro_schema["fields"].index(column)) + 1,
                    "qualifiedName":( hive_db+"."+table_name+"."+column["name"] + "@hdp_dev").lower()
                }
            }
            bulk_json.append(columns_json)


        bulk_json = {"entities": bulk_json}
        bulk_json = json.dumps(bulk_json, ensure_ascii=False)

        r = requests.post(URL + "/api/atlas/v2/entity/bulk", bulk_json,auth=("ozl","qaz1!wsx"),
                          headers=post_headers,verify="/etc/ssl/certs/AmdocsIntermediateCA.PEM")
        if r.status_code / 100 != 2:
            text = bulk_json
            raise Exception("Atlas returned code " + str(r.status_code) + " " + r.text)

        response_text = r.text


        response_text_json = json.loads(response_text)
        guid_list = []
        for guid in response_text_json["guidAssignments"].values():
            guid_list.append(guid)

        cluster_json = {
            "entity": {
                "typeName": "hive_db",
		"createTime": int(time.time() * 1000),
		"modifiedTime": int(time.time() * 1000),
                "attributes": {
		    "name":hive_db,
		     "owner":"nifi",
                    "clusterName": hive_db,
		    "qualifiedName":(hive_db+"@hdp_dev").lower()
                }
            }
        }

        cluster_json = json.dumps(cluster_json, ensure_ascii=False)
        r = requests.post(URL + "/api/atlas/v2/entity", cluster_json,auth=("ozl","qaz1!wsx"),
                          headers=post_headers,verify="/etc/ssl/certs/AmdocsIntermediateCA.PEM")

        if r.status_code / 100 != 2:
            text = cluster_json
            raise Exception("Atlas returned code " + str(r.status_code )+ " " + r.text)

        cluster_guid = str(r.text)

        guid_loc = (cluster_guid.find("guidAssignments")) + 17
        cluster_guid = cluster_guid[guid_loc:-1]
        cluster_guid = list(json.loads(cluster_guid, encoding='utf-8').values())

        rdbms_to_atlas = {
            "entity": {
                "typeName": "hive_table",
		"createTime": int(time.time() * 1000),
		"modifiedTime": int(time.time() * 1000),
                "attributes": {
                    "name": table_name,
		    "owner":"nifi",
                    "last_update": int(time.time() * 1000),
                    "qualifiedName": (hive_db+"."+table_name+"@hdp_dev").lower(),
                    "db": {
                        "typeName": "hive_db",
                        "guid": cluster_guid[0]
                    }
                }
            }
        }

        for guid in range(0, len(guid_list)):
            location = {"typeName": "hive_column", "guid": str(guid_list[guid])}
            cols.append(location)
        rdbms_to_atlas["entity"]["attributes"]["columns"] = cols

        rdbms_to_atlas = json.dumps(rdbms_to_atlas, ensure_ascii=False)
        r = requests.post(URL + "/api/atlas/v2/entity", rdbms_to_atlas,auth=("ozl","qaz1!wsx"),
                          headers=post_headers,verify="/etc/ssl/certs/AmdocsIntermediateCA.PEM")
        if r.status_code / 100 != 2:
	    text=rdbms_to_atlas
            raise Exception("Atlas returned code " +str(r.status_code) + " " + r.text)


	session.transfer(flowfile, REL_SUCCESS)

    except Exception as ERROR:
        class PyOutputStreamCallback(OutputStreamCallback):
            def __init__(self):
                pass

            def process(self, outputStream):
                outputStream.write(
                    "there has been error at: " + datetime.now().strftime("%Y-%m-%d %H:%M:%S") + "message- " + str(ERROR)+" "+text)


        flowfile = session.write(flowfile, PyOutputStreamCallback())
        session.transfer(flowfile, REL_FAILURE)

