import json
try:
    flowFile=session.get()
    if(flowFile):
        column_type=json.loads(flowFile.getAttribute('column_type'))
        column_name=json.loads(flowFile.getAttribute('column_name'))
        column_number=json.loads(flowFile.getAttribute('column_number'))
        data_scale=json.loads(flowFile.getAttribute('data_scale'))

        for loc in range(0,len(data_scale)):
            if data_scale[loc] !=None:
                if int(data_scale[loc])>int(0) :
                    column_type[column_name.index(column_number[loc])]='FLOAT'


        flowFile=session.putAttribute(flowFile,'column_type',json.dumps(column_type))
        session.transfer(flowFile, REL_SUCCESS)


except Exception as ERROR:
    flowFile=session.putAttribute(flowFile,'typeErr',str(ERROR))
    session.transfer(flowFile, REL_FAILURE)

