    column_name= json.loads(column_name)
    column_type= json.loads(column_type)
    file_type=(file_path.split(".",1)[1]).upper()


    table_name=flowfile.getAttribute('table_name')
    hdfs_path=flowfile.getAttribute('hdfs_path')
    cols=[]
